var chart = null;
var utils = null;

var chartContainer = null;
var zoomDebounceTimer = null;
var isExternalUpdate = false;

self.onInit = function() {
    // Get reference to ECOWidgetUtils library
    utils = window.ECOWidgetUtils;
    if (!utils) {
        console.error('ECOWidgetUtils library not loaded');
        return;
    }

    chartContainer = self.ctx.$container.find('#chart-container')[0];
    if (!chartContainer) {
        console.error('Chart container not found');
        return;
    }

    if (typeof echarts === 'undefined') {
        console.error('ECharts not loaded');
        return;
    }

    chart = echarts.init(chartContainer);

    var settings = self.ctx.settings || {};
    var showTimewindowSelector = settings.showTimewindowSelector === true;

    // Initialize timewindow selector using library
    if (showTimewindowSelector && self.ctx.dashboard) {
        var twContainer = self.ctx.$container.find('#timewindow-selector')[0];
        if (twContainer) {
            utils.timewindow.init(twContainer, {
                color: settings.twSelectorColor || '',
                position: settings.twSelectorPosition || 'center',
                dayFormat: settings.twSelectorDayFormat || 'D MMM YYYY',
                weekFormat: settings.twSelectorWeekFormat || 'D-D MMM',
                monthFormat: settings.twSelectorMonthFormat || 'MMM YYYY',
                customStartTime: settings.twCustomStartTime || '',
                customEndTime: settings.twCustomEndTime || '',
                aggregationType: settings.twAggregationType || 'NONE',
                maxDataPoints: settings.twMaxDataPoints || 100000
            }, self.ctx);
            utils.timewindow.render();
        }
    }

    if (settings.enableZoomSync !== false) {
        chart.on('datazoom', handleZoomSync);
    }

    updateChart();
};

function handleZoomSync(params) {
    if (isExternalUpdate) return;

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync === false) return;

    var debounceMs = settings.zoomSyncDebounce || 150;

    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }

    zoomDebounceTimer = setTimeout(function() {
        var option = chart.getOption();
        var dataZoom = option.dataZoom && option.dataZoom[0];
        if (!dataZoom) return;

        var timeWindow = self.ctx.timeWindow;
        if (!timeWindow) return;

        var totalRange = timeWindow.maxTime - timeWindow.minTime;
        var startPercent = dataZoom.start || 0;
        var endPercent = dataZoom.end || 100;

        if (startPercent > 0.5 || endPercent < 99.5) {
            var startTime = Math.round(timeWindow.minTime + (totalRange * startPercent / 100));
            var endTime = Math.round(timeWindow.minTime + (totalRange * endPercent / 100));

            if (self.ctx.dashboard && self.ctx.dashboard.onUpdateTimewindow) {
                self.ctx.dashboard.onUpdateTimewindow(startTime, endTime);
            }
        }
    }, debounceMs);
}

self.onDataUpdated = function() {
    isExternalUpdate = true;
    updateChart();
    setTimeout(function() { isExternalUpdate = false; }, 100);
};

// Calculate boxplot statistics using library percentile function
function calculateBoxplotStats(values) {
    if (!values || values.length === 0 || !utils) return null;

    var sorted = values.slice().sort(function(a, b) { return a - b; });

    // Use library percentile function (note: library uses 0-100 scale)
    var q1 = utils.stats.percentile(sorted, 25);
    var median = utils.stats.percentile(sorted, 50);
    var q3 = utils.stats.percentile(sorted, 75);
    var iqr = q3 - q1;
    var lowerFence = q1 - 1.5 * iqr;
    var upperFence = q3 + 1.5 * iqr;

    var min = sorted[0];
    var max = sorted[sorted.length - 1];

    // Whiskers extend to the most extreme data point within the fences
    var whiskerMin = min;
    var whiskerMax = max;
    var outliers = [];

    for (var i = 0; i < sorted.length; i++) {
        if (sorted[i] < lowerFence) {
            outliers.push(sorted[i]);
        } else {
            whiskerMin = sorted[i];
            break;
        }
    }

    for (var i = sorted.length - 1; i >= 0; i--) {
        if (sorted[i] > upperFence) {
            outliers.push(sorted[i]);
        } else {
            whiskerMax = sorted[i];
            break;
        }
    }

    return {
        min: whiskerMin,
        q1: q1,
        median: median,
        q3: q3,
        max: whiskerMax,
        outliers: outliers
    };
}

function getGroupKey(timestamp, groupBy) {
    var d = new Date(timestamp);
    switch (groupBy) {
        case 'hour':
            return d.getFullYear() + '-' + (d.getMonth() + 1) + '-' + d.getDate() + ' ' + d.getHours() + ':00';
        case 'day':
            return d.getFullYear() + '-' + (d.getMonth() + 1) + '-' + d.getDate();
        case 'week':
            var startOfYear = new Date(d.getFullYear(), 0, 1);
            var weekNum = Math.ceil(((d - startOfYear) / 86400000 + startOfYear.getDay() + 1) / 7);
            return d.getFullYear() + '-W' + weekNum;
        case 'month':
            return d.getFullYear() + '-' + (d.getMonth() + 1);
        default:
            return d.getFullYear() + '-' + (d.getMonth() + 1) + '-' + d.getDate();
    }
}

function updateChart() {
    if (!chart || !utils) return;

    var settings = self.ctx.settings || {};
    var dataMode = settings.dataMode || 'raw';
    var groupBy = settings.groupBy || 'day';
    var boxColor = settings.boxColor || '#5470C6';
    var borderColor = settings.borderColor || '#5470C6';
    var whiskerColor = settings.whiskerColor || '#5470C6';
    var showOutliers = settings.showOutliers !== false;
    var outlierColor = settings.outlierColor || '#E91E63';
    var showDataZoomSlider = settings.showDataZoomSlider !== false;
    var showLegend = settings.showLegend !== false;
    var showTooltip = settings.showTooltip !== false;
    var enableExport = settings.enableExport !== false;

    var data = self.ctx.data || [];
    if (!data.length) {
        chart.setOption({
            title: { text: 'No data', left: 'center', top: 'middle' }
        });
        return;
    }

    var boxplotData = [];
    var outlierData = [];
    var categoryData = [];
    var units = '';

    if (dataMode === 'precalculated') {
        // Precalculated mode: expect min, q1, median, q3, max data keys
        var minData = null, q1Data = null, medianData = null, q3Data = null, maxData = null;

        for (var i = 0; i < data.length; i++) {
            var ds = data[i];
            var keyName = ((ds.dataKey && ds.dataKey.name) || '').toLowerCase();
            var keyLabel = ((ds.dataKey && ds.dataKey.label) || '').toLowerCase();

            if (keyName === 'min' || keyLabel === 'min') minData = ds;
            else if (keyName === 'q1' || keyLabel === 'q1') q1Data = ds;
            else if (keyName === 'median' || keyLabel === 'median') medianData = ds;
            else if (keyName === 'q3' || keyLabel === 'q3') q3Data = ds;
            else if (keyName === 'max' || keyLabel === 'max') maxData = ds;
        }

        if (!minData || !q1Data || !medianData || !q3Data || !maxData) {
            chart.setOption({
                title: { text: 'Need min, q1, median, q3, max data keys', left: 'center', top: 'middle', textStyle: { fontSize: 11 } }
            });
            return;
        }

        units = (minData.dataKey && minData.dataKey.units) || '';

        // Build maps
        var maps = { min: {}, q1: {}, median: {}, q3: {}, max: {} };
        [['min', minData], ['q1', q1Data], ['median', medianData], ['q3', q3Data], ['max', maxData]].forEach(function(pair) {
            if (pair[1] && pair[1].data) {
                pair[1].data.forEach(function(d) { maps[pair[0]][d[0]] = d[1]; });
            }
        });

        var timestamps = {};
        [minData, q1Data, medianData, q3Data, maxData].forEach(function(ds) {
            if (ds && ds.data) ds.data.forEach(function(d) { timestamps[d[0]] = true; });
        });

        var sortedTs = Object.keys(timestamps).map(Number).sort(function(a, b) { return a - b; });

        sortedTs.forEach(function(ts) {
            var min = maps.min[ts], q1 = maps.q1[ts], med = maps.median[ts], q3 = maps.q3[ts], max = maps.max[ts];
            if (min !== undefined && q1 !== undefined && med !== undefined && q3 !== undefined && max !== undefined) {
                categoryData.push(getGroupKey(ts, groupBy));
                boxplotData.push([min, q1, med, q3, max]);
            }
        });
    } else {
        // Raw mode: group data and calculate statistics
        var valueData = data[0];
        if (!valueData || !valueData.data || !valueData.data.length) {
            chart.setOption({
                title: { text: 'No valid data', left: 'center', top: 'middle' }
            });
            return;
        }

        units = (valueData.dataKey && valueData.dataKey.units) || '';

        // Auto-detect groupBy if set to 'auto'
        if (groupBy === 'auto') {
            var timeRange = self.ctx.timeWindow ? (self.ctx.timeWindow.maxTime - self.ctx.timeWindow.minTime) : 0;
            if (timeRange > 30 * 86400000) groupBy = 'month';
            else if (timeRange > 7 * 86400000) groupBy = 'week';
            else if (timeRange > 86400000) groupBy = 'day';
            else groupBy = 'hour';
        }

        // Group values by time period
        var groups = {};
        valueData.data.forEach(function(d) {
            if (d[1] === null || d[1] === undefined || isNaN(d[1])) return;
            var key = getGroupKey(d[0], groupBy);
            if (!groups[key]) groups[key] = [];
            groups[key].push(d[1]);
        });

        // Calculate boxplot stats for each group
        var sortedKeys = Object.keys(groups).sort();
        sortedKeys.forEach(function(key) {
            var stats = calculateBoxplotStats(groups[key]);
            if (stats) {
                categoryData.push(key);
                boxplotData.push([stats.min, stats.q1, stats.median, stats.q3, stats.max]);
                if (showOutliers && stats.outliers.length > 0) {
                    stats.outliers.forEach(function(o) {
                        outlierData.push([categoryData.length - 1, o]);
                    });
                }
            }
        });
    }

    if (boxplotData.length === 0) {
        chart.setOption({
            title: { text: 'No valid boxplot data', left: 'center', top: 'middle' }
        });
        return;
    }

    var series = [{
        name: 'Boxplot',
        type: 'boxplot',
        data: boxplotData,
        itemStyle: {
            color: boxColor,
            borderColor: borderColor
        },
        boxWidth: ['40%', '70%']
    }];

    if (showOutliers && outlierData.length > 0) {
        series.push({
            name: 'Outliers',
            type: 'scatter',
            data: outlierData,
            itemStyle: {
                color: outlierColor
            },
            symbolSize: 6
        });
    }

    var option = {
        tooltip: showTooltip ? {
            trigger: 'item',
            formatter: function(params) {
                if (params.seriesType === 'boxplot') {
                    return params.name + '<br/>' +
                        'Max: ' + utils.format.value(params.data[5], 2) + ' ' + units + '<br/>' +
                        'Q3: ' + utils.format.value(params.data[4], 2) + ' ' + units + '<br/>' +
                        'Median: ' + utils.format.value(params.data[3], 2) + ' ' + units + '<br/>' +
                        'Q1: ' + utils.format.value(params.data[2], 2) + ' ' + units + '<br/>' +
                        'Min: ' + utils.format.value(params.data[1], 2) + ' ' + units;
                } else {
                    return 'Outlier: ' + utils.format.value(params.data[1], 2) + ' ' + units;
                }
            }
        } : {},
        legend: showLegend ? {
            data: showOutliers && outlierData.length > 0 ? ['Boxplot', 'Outliers'] : ['Boxplot'],
            top: 5
        } : {},
        toolbox: enableExport ? {
            feature: {
                saveAsImage: { title: 'Save as PNG' },
                dataView: { title: 'Data View', readOnly: true }
            },
            right: 10
        } : {},
        grid: {
            left: 60,
            right: enableExport ? 80 : 20,
            top: showLegend ? 40 : 20,
            bottom: showDataZoomSlider ? 60 : 40
        },
        xAxis: {
            type: 'category',
            data: categoryData,
            axisLabel: { fontSize: 9, rotate: categoryData.length > 10 ? 45 : 0 },
            splitLine: { show: false }
        },
        yAxis: {
            type: 'value',
            name: units,
            axisLabel: { fontSize: 10 },
            splitLine: { lineStyle: { type: 'dashed', opacity: 0.3 } }
        },
        dataZoom: [
            { type: 'inside', xAxisIndex: 0 },
            showDataZoomSlider ? { type: 'slider', xAxisIndex: 0, bottom: 5, height: 20 } : null
        ].filter(function(x) { return x !== null; }),
        series: series
    };

    chart.setOption(option, true);
}

self.onResize = function() {
    if (chart) {
        chart.resize();
    }
};

self.onDestroy = function() {
    if (chart) {
        chart.off('datazoom');
        chart.dispose();
        chart = null;
    }
    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }
};

self.typeParameters = function() {
    return {
        previewWidth: '80%',
        embedTitlePanel: true,
        dataKeySettingsFunction: function() { return {}; },
        defaultDataKeysFunction: function() {
            return [{ name: 'value', label: 'Value', type: 'timeseries' }];
        }
    };
};

