descriptor.controllerScript:
var chart = null;
var chartContainer = null;
var timewindowSelectorContainer = null;

// Reference to ECOWidgetUtils library
var utils = null;

self.onInit = function() {
    // Get library reference
    utils = window.ECOWidgetUtils;
    if (!utils) {
        console.error('ECOWidgetUtils library not loaded');
        return;
    }

    chartContainer = self.ctx.$container.find('#chart-container')[0];
    timewindowSelectorContainer = self.ctx.$container.find('#timewindow-selector')[0];

    if (!chartContainer) {
        console.error('Chart container not found');
        return;
    }

    if (typeof echarts === 'undefined') {
        console.error('ECharts not loaded');
        return;
    }

    chart = echarts.init(chartContainer);

    // Initialize timewindow selector
    var settings = self.ctx.settings || {};
    if (timewindowSelectorContainer) {
        utils.timewindow.init(timewindowSelectorContainer, {
            color: settings.twSelectorColor || '',
            position: settings.twSelectorPosition || 'center',
            dayFormat: settings.twSelectorDayFormat || 'D MMM YYYY',
            weekFormat: settings.twSelectorWeekFormat || 'D-D MMM',
            monthFormat: settings.twSelectorMonthFormat || 'MMM YYYY',
            customStartTime: settings.twCustomStartTime || '',
            customEndTime: settings.twCustomEndTime || '',
            aggregationType: settings.twAggregationType || 'NONE',
            maxDataPoints: settings.twMaxDataPoints || 100000
        }, self.ctx);
    }

    updateChart();
};

self.onDataUpdated = function() {
    updateChart();
};

function getColorScheme(scheme, minColor, maxColor) {
    switch (scheme) {
        case 'github':
            return ['#ebedf0', '#9be9a8', '#40c463', '#30a14e', '#216e39'];
        case 'blue':
            return ['#E3F2FD', '#90CAF9', '#42A5F5', '#1E88E5', '#0D47A1'];
        case 'green':
            return ['#E8F5E9', '#A5D6A7', '#66BB6A', '#43A047', '#1B5E20'];
        case 'red':
            return ['#FFEBEE', '#EF9A9A', '#EF5350', '#E53935', '#B71C1C'];
        case 'purple':
            return ['#F3E5F5', '#CE93D8', '#AB47BC', '#8E24AA', '#4A148C'];
        case 'custom':
            return [minColor || '#ebedf0', maxColor || '#216e39'];
        default:
            return ['#ebedf0', '#9be9a8', '#40c463', '#30a14e', '#216e39'];
    }
}

function updateChart() {
    if (!chart || !utils) return;

    var settings = self.ctx.settings || {};

    // Handle timewindow selector
    var showTimewindowSelector = settings.showTimewindowSelector === true;
    if (showTimewindowSelector && self.ctx.dashboard && timewindowSelectorContainer) {
        // Re-init with current settings in case they changed
        utils.timewindow.init(timewindowSelectorContainer, {
            color: settings.twSelectorColor || '',
            position: settings.twSelectorPosition || 'center',
            dayFormat: settings.twSelectorDayFormat || 'D MMM YYYY',
            weekFormat: settings.twSelectorWeekFormat || 'D-D MMM',
            monthFormat: settings.twSelectorMonthFormat || 'MMM YYYY',
            customStartTime: settings.twCustomStartTime || '',
            customEndTime: settings.twCustomEndTime || '',
            aggregationType: settings.twAggregationType || 'NONE',
            maxDataPoints: settings.twMaxDataPoints || 100000
        }, self.ctx);
        utils.timewindow.render();
    } else {
        utils.timewindow.hide();
    }

    var yearDisplay = settings.yearDisplay || 'auto';
    var customYear = settings.customYear || new Date().getFullYear();
    var colorScheme = settings.colorScheme || 'github';
    var minColor = settings.minColor;
    var maxColor = settings.maxColor;
    var cellSize = settings.cellSize || 14;
    var showMonthLabel = settings.showMonthLabel !== false;
    var showDayLabel = settings.showDayLabel !== false;
    var showVisualMap = settings.showVisualMap !== false;
    var showTooltip = settings.showTooltip !== false;
    var enableExport = settings.enableExport !== false;

    var data = self.ctx.data || [];
    if (!data.length || !data[0].data || !data[0].data.length) {
        chart.setOption({
            title: { text: 'No data', left: 'center', top: 'middle' }
        });
        return;
    }

    var valueData = data[0];
    var units = (valueData.dataKey && valueData.dataKey.units) || '';
    var decimals = (valueData.dataKey && valueData.dataKey.decimals !== undefined) ? valueData.dataKey.decimals : 2;

    // Determine year to display
    var displayYear;
    if (yearDisplay === 'current') {
        displayYear = new Date().getFullYear();
    } else if (yearDisplay === 'previous') {
        displayYear = new Date().getFullYear() - 1;
    } else if (yearDisplay === 'custom') {
        displayYear = customYear;
    } else {
        // Auto: use year from data
        var firstTs = valueData.data[0][0];
        displayYear = new Date(firstTs).getFullYear();
    }

    // Aggregate data by day using library format function
    var dailyData = {};
    valueData.data.forEach(function(d) {
        if (d[1] === null || d[1] === undefined || isNaN(d[1])) return;
        var dateKey = utils.format.timestamp(d[0], 'YYYY-MM-DD');
        if (!dailyData[dateKey]) {
            dailyData[dateKey] = { sum: 0, count: 0 };
        }
        dailyData[dateKey].sum += d[1];
        dailyData[dateKey].count++;
    });

    // Build calendar data array
    var calendarData = [];
    var allValues = [];

    Object.keys(dailyData).forEach(function(dateKey) {
        var avg = dailyData[dateKey].sum / dailyData[dateKey].count;
        calendarData.push([dateKey, avg]);
        allValues.push(avg);
    });

    if (calendarData.length === 0) {
        chart.setOption({
            title: { text: 'No valid calendar data', left: 'center', top: 'middle' }
        });
        return;
    }

    // Use library stats to get min/max
    var stats = utils.stats.calculate(allValues);
    var dataMin = stats.min;
    var dataMax = stats.max;
    var colorRange = getColorScheme(colorScheme, minColor, maxColor);

    // Calculate dimensions
    var calendarRange = displayYear.toString();

    var option = {
        tooltip: showTooltip ? {
            formatter: function(params) {
                return params.data[0] + '<br/>Value: ' + utils.format.value(params.data[1], decimals) + ' ' + units;
            }
        } : {},
        toolbox: enableExport ? {
            feature: {
                saveAsImage: { title: 'Save as PNG' },
                dataView: { title: 'Data View', readOnly: true }
            },
            right: 10
        } : {},
        visualMap: showVisualMap ? {
            min: dataMin,
            max: dataMax,
            calculable: true,
            orient: 'horizontal',
            left: 'center',
            bottom: 5,
            inRange: {
                color: colorRange
            },
            text: ['High', 'Low'],
            textStyle: { fontSize: 10 }
        } : {
            show: false,
            min: dataMin,
            max: dataMax,
            inRange: { color: colorRange }
        },
        calendar: {
            top: showMonthLabel ? 40 : 20,
            left: showDayLabel ? 50 : 30,
            right: enableExport ? 60 : 30,
            bottom: showVisualMap ? 60 : 20,
            cellSize: [cellSize, cellSize],
            range: calendarRange,
            itemStyle: {
                borderWidth: 0.5,
                borderColor: '#fff'
            },
            yearLabel: { show: true, margin: 30 },
            monthLabel: { show: showMonthLabel, fontSize: 10 },
            dayLabel: { show: showDayLabel, fontSize: 10, firstDay: 1, nameMap: 'en' },
            splitLine: {
                show: true,
                lineStyle: {
                    color: '#ccc',
                    width: 1
                }
            }
        },
        series: [{
            type: 'heatmap',
            coordinateSystem: 'calendar',
            data: calendarData,
            emphasis: {
                itemStyle: {
                    shadowBlur: 10,
                    shadowColor: 'rgba(0, 0, 0, 0.5)'
                }
            }
        }]
    };

    chart.setOption(option, true);
}

self.onResize = function() {
    if (chart) {
        chart.resize();
    }
};

self.onDestroy = function() {
    if (chart) {
        chart.dispose();
        chart = null;
    }
};

self.typeParameters = function() {
    return {
        previewWidth: '100%',
        embedTitlePanel: true,
        dataKeySettingsFunction: function() { return {}; },
        defaultDataKeysFunction: function() {
            return [{ name: 'value', label: 'Daily Value', type: 'timeseries' }];
        }
    };
};

