descriptor.controllerScript:
/**
 * ECO Candlestick Basic Widget (Library Version)
 *
 * OHLC Candlestick chart with Moving Average overlay,
 * statistics cards, data processing, and timewindow selector.
 *
 * Uses ECOWidgetUtils library for common functionality.
 */

var utils = window.ECOWidgetUtils;

var chart = null;
var chartContainer = null;
var statsCardContainers = {};
var timewindowSelectorContainer = null;
var resizeObserver = null;
var zoomDebounceTimer = null;
var widgetId = null;
var isExternalUpdate = false;

self.onInit = function() {
    chartContainer = self.ctx.$container.find('#chart-container')[0];
    timewindowSelectorContainer = self.ctx.$container.find('#timewindow-selector')[0];
    statsCardContainers = {
        top: self.ctx.$container.find('#stats-card-top')[0],
        bottom: self.ctx.$container.find('#stats-card-bottom')[0],
        left: self.ctx.$container.find('#stats-card-left')[0],
        right: self.ctx.$container.find('#stats-card-right')[0]
    };

    if (!chartContainer) {
        console.error('ECO Candlestick Basic: Chart container not found');
        return;
    }

    if (typeof echarts === 'undefined') {
        console.error('ECO Candlestick Basic: ECharts library not loaded');
        return;
    }

    if (!utils) {
        console.error('ECO Candlestick Basic: ECOWidgetUtils library not loaded');
        return;
    }

    chart = echarts.init(chartContainer);

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync !== false) {
        chart.on('datazoom', handleZoomSync);

        // Subscribe to zoom sync from other widgets
        if (utils && utils.zoomSync) {
            utils.zoomSync.subscribe(widgetId, function(start, end) {
                isExternalUpdate = true;
                chart.dispatchAction({ type: 'dataZoom', start: start, end: end });
                setTimeout(function() { isExternalUpdate = false; }, 100);
            });
        }
    }

    updateChart();

    // Multiple delayed resizes for ThingsBoard's async layout
    [100, 250, 500, 1000].forEach(function(delay) {
        setTimeout(function() {
            if (chart && chartContainer) {
                chart.resize({
                    width: chartContainer.offsetWidth,
                    height: chartContainer.offsetHeight
                });
            }
        }, delay);
    });

    // ResizeObserver for dynamic container changes
    if (typeof ResizeObserver !== 'undefined') {
        resizeObserver = new ResizeObserver(function() {
            requestAnimationFrame(function() {
                if (chart && chartContainer) {
                    chart.resize({
                        width: chartContainer.offsetWidth,
                        height: chartContainer.offsetHeight
                    });
                }
            });
        });
        resizeObserver.observe(chartContainer);
    }
};

function handleZoomSync(params) {
    if (isExternalUpdate) return;

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync === false) return;

    var debounceMs = settings.zoomSyncDebounce || 150;

    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }

    zoomDebounceTimer = setTimeout(function() {
        var option = chart.getOption();
        var dataZoom = option.dataZoom && option.dataZoom[0];
        if (!dataZoom) return;

        var timeWindow = self.ctx.timeWindow;
        if (!timeWindow) return;

        var startPercent = dataZoom.start !== undefined ? dataZoom.start : 0;
        var endPercent = dataZoom.end !== undefined ? dataZoom.end : 100;

        // Broadcast zoom to other widgets

            if (utils && utils.zoomSync) { utils.zoomSync.broadcast(widgetId, startPercent, endPercent); }
        }
    }, debounceMs);
}

self.onDataUpdated = function() {
    isExternalUpdate = true;
    updateChart();
    setTimeout(function() { isExternalUpdate = false; }, 100);
};

function updateChart() {
    if (!chart) return;

    var settings = self.ctx.settings || {};
    var data = self.ctx.data || [];

    // Candlestick colors
    var upColor = settings.upColor || '#26A69A';
    var downColor = settings.downColor || '#EF5350';
    var upBorderColor = settings.upBorderColor || '#26A69A';
    var downBorderColor = settings.downBorderColor || '#EF5350';

    // Moving Average settings
    var showMA = settings.showMA || false;
    var maPeriod = settings.maPeriod || 5;
    var maColor = settings.maColor || '#FF9800';

    // Legend settings
    var showLegend = settings.showLegend !== false;
    var legendStyle = settings.legendStyle || 'classic';
    var legendPosition = settings.legendPosition || 'bottom';
    var legendAlign = settings.legendAlign || 'center';
    var legendCardColorMode = settings.legendCardColorMode || 'auto';
    var legendCardColor = settings.legendCardColor || '#2196F3';
    var legendValues = settings.legendValues || ['current'];

    // Timestamp settings
    var showTimestamp = settings.showTimestamp !== false;
    var timestampFormat = settings.timestampFormat || 'YYYY-MM-DD HH:mm:ss';

    // DataZoom settings
    var showDataZoomSlider = settings.showDataZoomSlider !== false;

    // Outlier removal settings
    var removeOutliers = settings.removeOutliers === true;
    var outlierMethod = settings.outlierMethod || 'iqr';
    var outlierIqrMultiplier = settings.outlierIqrMultiplier || 1.5;
    var outlierZscoreThreshold = settings.outlierZscoreThreshold || 3;
    var outlierMinValue = settings.outlierMinValue;
    var outlierMaxValue = settings.outlierMaxValue;

    // Smoothing settings
    var smoothingEnabled = settings.smoothingEnabled === true;
    var smoothingWindowMinutes = settings.smoothingWindowMinutes || 15;

    // Toolbox settings
    var showToolbox = settings.showToolbox !== false;
    var toolboxFeatures = settings.toolboxFeatures || ['saveAsImage', 'dataView', 'dataZoom', 'restore'];

    // Timewindow selector settings
    var showTimewindowSelector = settings.showTimewindowSelector === true;

    // Initialize and render timewindow selector using library
    if (showTimewindowSelector && self.ctx.dashboard && timewindowSelectorContainer) {
        utils.timewindow.init(timewindowSelectorContainer, {
            color: settings.twSelectorColor || '',
            position: settings.twSelectorPosition || 'center',
            dayFormat: settings.twSelectorDayFormat || 'D MMM YYYY',
            weekFormat: settings.twSelectorWeekFormat || 'D-D MMM',
            monthFormat: settings.twSelectorMonthFormat || 'MMM YYYY',
            customStartTime: settings.twCustomStartTime || '',
            customEndTime: settings.twCustomEndTime || '',
            aggregationType: settings.twAggregationType || 'NONE',
            maxDataPoints: settings.twMaxDataPoints || 100000
        }, self.ctx);
        utils.timewindow.render();
    } else if (timewindowSelectorContainer) {
        utils.timewindow.hide();
    }

    // Check for valid data
    if (!data.length) {
        showNoData('No data available');
        return;
    }

    // Find open, high, low, close data keys
    var openData = null, highData = null, lowData = null, closeData = null;

    for (var i = 0; i < data.length; i++) {
        var ds = data[i];
        var keyName = ((ds.dataKey && ds.dataKey.name) || '').toLowerCase();
        var keyLabel = ((ds.dataKey && ds.dataKey.label) || '').toLowerCase();

        if (keyName === 'open' || keyLabel === 'open') openData = ds;
        else if (keyName === 'high' || keyLabel === 'high') highData = ds;
        else if (keyName === 'low' || keyLabel === 'low') lowData = ds;
        else if (keyName === 'close' || keyLabel === 'close') closeData = ds;
    }

    // Fallback: use first 4 series as open, high, low, close
    if ((!openData || !highData || !lowData || !closeData) && data.length >= 4) {
        openData = data[0];
        highData = data[1];
        lowData = data[2];
        closeData = data[3];
    }

    if (!openData || !closeData) {
        showNoData('Need open, high, low, close data keys');
        return;
    }

    // Build OHLC data maps
    var openMap = {}, highMap = {}, lowMap = {}, closeMap = {};

    if (openData && openData.data) openData.data.forEach(function(d) { openMap[d[0]] = d[1]; });
    if (highData && highData.data) highData.data.forEach(function(d) { highMap[d[0]] = d[1]; });
    if (lowData && lowData.data) lowData.data.forEach(function(d) { lowMap[d[0]] = d[1]; });
    if (closeData && closeData.data) closeData.data.forEach(function(d) { closeMap[d[0]] = d[1]; });

    // Get all unique timestamps
    var timestamps = {};
    [openData, highData, lowData, closeData].forEach(function(ds) {
        if (ds && ds.data) {
            ds.data.forEach(function(d) { timestamps[d[0]] = true; });
        }
    });

    var sortedTimestamps = Object.keys(timestamps).map(Number).sort(function(a, b) { return a - b; });

    // Build candlestick data with optional outlier removal
    var candleData = [];
    var categoryData = [];
    var closeValues = [];
    var lastCloseValue = null;
    var lastTimestamp = null;

    sortedTimestamps.forEach(function(ts) {
        var o = openMap[ts], h = highMap[ts], l = lowMap[ts], c = closeMap[ts];
        if (o !== undefined && c !== undefined) {
            h = h !== undefined ? h : Math.max(o, c);
            l = l !== undefined ? l : Math.min(o, c);

            // Apply outlier removal to close values if enabled
            if (removeOutliers) {
                var testValues = [c];
                var testTimestamps = [ts];
                var outlierResult = utils.dataProcessing.removeOutliers(testValues, testTimestamps, {
                    method: outlierMethod,
                    iqrMultiplier: outlierIqrMultiplier,
                    zscoreThreshold: outlierZscoreThreshold,
                    minValue: outlierMinValue,
                    maxValue: outlierMaxValue
                });
                if (outlierResult.values.length === 0) {
                    return; // Skip this data point
                }
            }

            var formattedTs = utils.format.timestamp(ts, 'YYYY-MM-DD HH:mm');
            categoryData.push(formattedTs);
            candleData.push([o, c, l, h]);
            closeValues.push(c);
            lastCloseValue = c;
            lastTimestamp = ts;
        }
    });

    if (candleData.length === 0) {
        showNoData('No valid OHLC data');
        return;
    }

    // Apply smoothing to close values if enabled (for MA calculation)
    var smoothedCloseValues = closeValues.slice();
    if (smoothingEnabled && smoothingWindowMinutes > 0 && sortedTimestamps.length > 1) {
        var windowSize = utils.dataProcessing.getWindowSizeFromMinutes(sortedTimestamps, smoothingWindowMinutes);
        smoothedCloseValues = utils.dataProcessing.movingAverage(closeValues, windowSize);
    }

    // Build series
    var series = [{
        name: 'Candlestick',
        type: 'candlestick',
        data: candleData,
        itemStyle: {
            color: upColor,
            color0: downColor,
            borderColor: upBorderColor,
            borderColor0: downBorderColor
        }
    }];

    var legendData = ['Candlestick'];

    // Calculate Moving Average if enabled
    if (showMA && maPeriod > 0 && candleData.length >= maPeriod) {
        var maData = [];
        for (var i = 0; i < smoothedCloseValues.length; i++) {
            if (i < maPeriod - 1) {
                maData.push('-');
            } else {
                var windowValues = [];
                for (var j = 0; j < maPeriod; j++) {
                    windowValues.push(smoothedCloseValues[i - j]);
                }
                var stats = utils.stats.calculate(windowValues);
                maData.push(utils.format.value(stats.mean, 2));
            }
        }

        series.push({
            name: 'MA' + maPeriod,
            type: 'line',
            data: maData,
            smooth: true,
            symbol: 'none',
            lineStyle: { color: maColor, width: 2 }
        });
        legendData.push('MA' + maPeriod);
    }

    // Calculate statistics for stats card
    var closeStats = utils.stats.calculate(closeValues);
    closeStats.current = lastCloseValue;
    closeStats.lastTimestamp = lastTimestamp;

    var allStats = [{
        label: 'Close',
        units: '',
        decimals: 2,
        color: upColor,
        stats: closeStats
    }];

    // Build ECharts option
    var option = buildChartOption({
        series: series,
        legendData: legendData,
        categoryData: categoryData,
        showLegend: showLegend && legendStyle === 'classic',
        showDataZoomSlider: showDataZoomSlider,
        showToolbox: showToolbox,
        toolboxFeatures: toolboxFeatures
    });

    chart.setOption(option, true);

    // Render statistics cards using library
    utils.statsCard.render(statsCardContainers, {
        showLegend: showLegend,
        legendStyle: legendStyle,
        legendAlign: legendAlign,
        legendCardColorMode: legendCardColorMode,
        legendCardColor: legendCardColor,
        legendValues: legendValues,
        legendPosition: legendPosition,
        showTimestamp: showTimestamp,
        timestampFormat: timestampFormat,
        allStats: allStats
    });

    // Resize chart after stats cards
    function doResize() {
        if (chart && chartContainer) {
            chart.resize({
                width: chartContainer.offsetWidth,
                height: chartContainer.offsetHeight
            });
        }
    }
    requestAnimationFrame(function() { requestAnimationFrame(doResize); });
    setTimeout(doResize, 100);
    setTimeout(doResize, 250);
}

function showNoData(message) {
    if (!chart) return;
    chart.setOption({
        title: {
            text: message,
            left: 'center',
            top: 'middle',
            textStyle: { color: '#999', fontSize: 14 }
        }
    }, true);
}

function buildChartOption(config) {
    var showLegend = config.showLegend;
    var legendData = config.legendData;

    var topMargin = showLegend ? 40 : 20;
    var bottomMargin = config.showDataZoomSlider ? 60 : 40;
    var rightMargin = config.showToolbox ? 80 : 20;

    // Build toolbox config
    var toolboxConfig = null;
    if (config.showToolbox) {
        var features = config.toolboxFeatures || [];
        toolboxConfig = { show: true, right: 10, top: 5, feature: {} };

        if (features.indexOf('saveAsImage') !== -1) {
            toolboxConfig.feature.saveAsImage = { show: true, title: 'Save as PNG', pixelRatio: 2 };
        }
        if (features.indexOf('dataView') !== -1) {
            toolboxConfig.feature.dataView = { show: true, title: 'Data View', readOnly: true };
        }
        if (features.indexOf('dataZoom') !== -1) {
            toolboxConfig.feature.dataZoom = { show: true, title: { zoom: 'Zoom', back: 'Reset Zoom' } };
        }
        if (features.indexOf('restore') !== -1) {
            toolboxConfig.feature.restore = { show: true, title: 'Restore' };
        }
    }

    return {
        tooltip: {
            trigger: 'axis',
            axisPointer: { type: 'cross' },
            formatter: function(params) {
                if (!params || !params.length) return '';
                var result = params[0].axisValue + '<br/>';
                for (var i = 0; i < params.length; i++) {
                    var p = params[i];
                    if (p.seriesType === 'candlestick' && p.data) {
                        result += 'Open: ' + utils.format.value(p.data[1], 2) + '<br/>';
                        result += 'Close: ' + utils.format.value(p.data[2], 2) + '<br/>';
                        result += 'Low: ' + utils.format.value(p.data[3], 2) + '<br/>';
                        result += 'High: ' + utils.format.value(p.data[4], 2) + '<br/>';
                    } else if (p.seriesType === 'line' && p.data !== '-') {
                        result += p.seriesName + ': ' + p.data + '<br/>';
                    }
                }
                return result;
            }
        },
        legend: showLegend ? {
            data: legendData,
            top: 5
        } : null,
        toolbox: toolboxConfig,
        grid: {
            left: 60,
            right: rightMargin,
            top: topMargin,
            bottom: bottomMargin
        },
        xAxis: {
            type: 'category',
            data: config.categoryData,
            axisLabel: { fontSize: 9, rotate: 30 },
            splitLine: { show: false }
        },
        yAxis: {
            type: 'value',
            scale: true,
            axisLabel: { fontSize: 10 },
            splitLine: { lineStyle: { type: 'dashed', opacity: 0.3 } }
        },
        dataZoom: [
            { type: 'inside', xAxisIndex: 0 },
            config.showDataZoomSlider ? { type: 'slider', xAxisIndex: 0, bottom: 5, height: 20 } : null
        ].filter(function(x) { return x !== null; }),
        series: config.series
    };
}

self.onResize = function() {
    if (chart && chartContainer) {
        chart.resize({
            width: chartContainer.offsetWidth,
            height: chartContainer.offsetHeight
        });
    }
};

self.onDestroy = function() {
    // Unsubscribe from zoom sync
    if (utils && utils.zoomSync && widgetId) {
        utils.zoomSync.unsubscribe(widgetId);
    }

    if (resizeObserver) {
        resizeObserver.disconnect();
        resizeObserver = null;
    }
    if (chart) {
        chart.off('datazoom');
        chart.dispose();
        chart = null;
    }
    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }
};

self.typeParameters = function() {
    return {
        previewWidth: '100%',
        previewHeight: '100%',
        embedTitlePanel: false,
        hasDataExportAction: true,
        dataKeySettingsFunction: function() { return {}; },
        defaultDataKeysFunction: function() {
            return [
                { name: 'open', label: 'Open', type: 'timeseries' },
                { name: 'high', label: 'High', type: 'timeseries' },
                { name: 'low', label: 'Low', type: 'timeseries' },
                { name: 'close', label: 'Close', type: 'timeseries' }
            ];
        }
    };
};

