descriptor.controllerScript:
/**
 * ECO Heatmap Cartesian Widget
 *
 * Cartesian heatmap displaying values on an X-Y grid with full ECO standard settings:
 * - Legend/Stats card rendering
 * - Data processing (outlier removal, smoothing)
 * - Toolbox configuration
 * - Timewindow selector
 */

var chart = null;
var utils = window.ECOWidgetUtils;

var chartContainer = null;
var statsCardContainers = {};
var timewindowSelectorContainer = null;
var resizeObserver = null;
var zoomDebounceTimer = null;
var widgetId = null;
var isExternalUpdate = false;

self.onInit = function() {
    chartContainer = self.ctx.$container.find('#chart-container')[0];
    timewindowSelectorContainer = self.ctx.$container.find('#timewindow-selector')[0];
    statsCardContainers = {
        top: self.ctx.$container.find('#stats-card-top')[0],
        bottom: self.ctx.$container.find('#stats-card-bottom')[0],
        left: self.ctx.$container.find('#stats-card-left')[0],
        right: self.ctx.$container.find('#stats-card-right')[0]
    };

    if (!chartContainer) {
        console.error('ECO Heatmap Cartesian: Chart container not found');
        return;
    }

    if (typeof echarts === 'undefined') {
        console.error('ECO Heatmap Cartesian: ECharts not loaded');
        return;
    }

    if (!utils) {
        console.error('ECO Heatmap Cartesian: ECOWidgetUtils library not loaded');
        return;
    }

    chart = echarts.init(chartContainer);

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync !== false) {
        chart.on('datazoom', handleZoomSync);

        // Subscribe to zoom sync from other widgets
        if (utils && utils.zoomSync) {
            utils.zoomSync.subscribe(widgetId, function(start, end) {
                isExternalUpdate = true;
                chart.dispatchAction({ type: 'dataZoom', start: start, end: end });
                setTimeout(function() { isExternalUpdate = false; }, 100);
            });
        }
    }

    updateChart();

    // Multiple delayed resizes for ThingsBoard's async layout
    [100, 250, 500, 1000].forEach(function(delay) {
        setTimeout(function() {
            if (chart && chartContainer) {
                chart.resize({
                    width: chartContainer.offsetWidth,
                    height: chartContainer.offsetHeight
                });
            }
        }, delay);
    });

    // ResizeObserver for dynamic container changes
    if (typeof ResizeObserver !== 'undefined') {
        resizeObserver = new ResizeObserver(function() {
            requestAnimationFrame(function() {
                if (chart && chartContainer) {
                    chart.resize({
                        width: chartContainer.offsetWidth,
                        height: chartContainer.offsetHeight
                    });
                }
            });
        });
        resizeObserver.observe(chartContainer);
    }
};

function handleZoomSync(params) {
    if (isExternalUpdate) return;

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync === false) return;

    var debounceMs = settings.zoomSyncDebounce || 150;

    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }

    zoomDebounceTimer = setTimeout(function() {
        var option = chart.getOption();
        var dataZoom = option.dataZoom && option.dataZoom[0];
        if (!dataZoom) return;

        var timeWindow = self.ctx.timeWindow;
        if (!timeWindow) return;

        var startPercent = dataZoom.start !== undefined ? dataZoom.start : 0;
        var endPercent = dataZoom.end !== undefined ? dataZoom.end : 100;

        // Broadcast zoom to other widgets

            if (utils && utils.zoomSync) { utils.zoomSync.broadcast(widgetId, startPercent, endPercent); }
        }
    }, debounceMs);
}

self.onDataUpdated = function() {
    isExternalUpdate = true;
    updateChart();
    setTimeout(function() { isExternalUpdate = false; }, 100);
};

function getTimeGroupKey(timestamp, grouping) {
    var d = new Date(timestamp);
    switch (grouping) {
        case 'hour':
            return d.getHours() + ':00';
        case 'dayOfWeek':
            return ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'][d.getDay()];
        case 'day':
            return d.getDate();
        case 'week':
            var startOfYear = new Date(d.getFullYear(), 0, 1);
            return 'W' + Math.ceil(((d - startOfYear) / 86400000 + startOfYear.getDay() + 1) / 7);
        case 'month':
            return ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'][d.getMonth()];
        default:
            return d.getFullYear() + '-' + (d.getMonth() + 1) + '-' + d.getDate();
    }
}

function getColorScheme(scheme, minColor, maxColor) {
    switch (scheme) {
        case 'blue':
            return ['#E3F2FD', '#2196F3', '#0D47A1'];
        case 'green':
            return ['#E8F5E9', '#4CAF50', '#1B5E20'];
        case 'red':
            return ['#FFEBEE', '#F44336', '#B71C1C'];
        case 'temperature':
            return ['#2196F3', '#FFEB3B', '#F44336'];
        case 'custom':
            return [minColor || '#E3F2FD', maxColor || '#0D47A1'];
        default:
            return ['#E3F2FD', '#2196F3', '#0D47A1'];
    }
}

function updateChart() {
    if (!chart) return;

    var settings = self.ctx.settings || {};

    // Chart settings
    var xAxisMode = settings.xAxisMode || 'time';
    var yAxisMode = settings.yAxisMode || 'entity';
    var timeGrouping = settings.timeGrouping || 'hour';
    var colorScheme = settings.colorScheme || 'blue';
    var minColor = settings.minColor || '#E3F2FD';
    var maxColor = settings.maxColor || '#0D47A1';
    var showVisualMap = settings.showVisualMap !== false;
    var minValue = settings.minValue;
    var maxValue = settings.maxValue;
    var showDataZoomSlider = settings.showDataZoomSlider !== false;
    var showTooltip = settings.showTooltip !== false;

    // Legend settings
    var showLegend = settings.showLegend !== false;
    var legendStyle = settings.legendStyle || 'classic';
    var legendPosition = settings.legendPosition || 'bottom';
    var legendAlign = settings.legendAlign || 'center';
    var legendCardColorMode = settings.legendCardColorMode || 'auto';
    var legendCardColor = settings.legendCardColor || '#2196F3';
    var legendValues = settings.legendValues || ['current'];
    var showTimestamp = settings.showTimestamp !== false;
    var timestampFormat = settings.timestampFormat || 'YYYY-MM-DD HH:mm:ss';

    // Data processing settings
    var removeOutliers = settings.removeOutliers === true;
    var outlierMethod = settings.outlierMethod || 'iqr';
    var outlierIqrMultiplier = settings.outlierIqrMultiplier || 1.5;
    var outlierZscoreThreshold = settings.outlierZscoreThreshold || 3;
    var outlierMinValue = settings.outlierMinValue;
    var outlierMaxValue = settings.outlierMaxValue;
    var smoothingEnabled = settings.smoothingEnabled === true;
    var smoothingWindowMinutes = settings.smoothingWindowMinutes || 15;

    // Toolbox settings
    var showToolbox = settings.showToolbox !== false;
    var toolboxFeatures = settings.toolboxFeatures || ['saveAsImage', 'dataView', 'dataZoom', 'restore'];

    // Timewindow selector settings
    var showTimewindowSelector = settings.showTimewindowSelector === true;

    // Initialize and render timewindow selector using library
    if (showTimewindowSelector && self.ctx.dashboard && timewindowSelectorContainer) {
        utils.timewindow.init(timewindowSelectorContainer, {
            color: settings.twSelectorColor || '',
            position: settings.twSelectorPosition || 'center',
            dayFormat: settings.twSelectorDayFormat || 'D MMM YYYY',
            weekFormat: settings.twSelectorWeekFormat || 'D-D MMM',
            monthFormat: settings.twSelectorMonthFormat || 'MMM YYYY',
            customStartTime: settings.twCustomStartTime || '',
            customEndTime: settings.twCustomEndTime || '',
            aggregationType: settings.twAggregationType || 'NONE',
            maxDataPoints: settings.twMaxDataPoints || 100000
        }, self.ctx);
        utils.timewindow.render();
    } else if (timewindowSelectorContainer) {
        utils.timewindow.hide();
    }

    var data = self.ctx.data || [];
    if (!data.length) {
        showNoData('No data available');
        return;
    }

    var xCategories = [];
    var yCategories = [];
    var heatmapData = [];
    var allValues = [];
    var units = '';
    var allStats = [];

    // Build aggregated data
    var aggregated = {}; // { xKey: { yKey: { sum: 0, count: 0, values: [] } } }

    for (var i = 0; i < data.length; i++) {
        var ds = data[i];
        if (!ds.data || !ds.data.length) continue;

        var dataKey = ds.dataKey || {};
        var entityLabel = dataKey.label || (ds.datasource && ds.datasource.name) || 'Series ' + (i + 1);
        var seriesUnits = dataKey.units || '';
        var seriesDecimals = dataKey.decimals !== undefined ? dataKey.decimals : 2;
        var seriesColor = dataKey.color || utils.color.getDefault(i);
        if (!units && seriesUnits) units = seriesUnits;

        if (yAxisMode === 'entity' && yCategories.indexOf(entityLabel) === -1) {
            yCategories.push(entityLabel);
        }

        // Extract raw values and timestamps for this series
        var rawValues = [];
        var timestamps = [];
        for (var j = 0; j < ds.data.length; j++) {
            var ts = ds.data[j][0];
            var val = ds.data[j][1];
            if (val !== null && val !== undefined && !isNaN(val)) {
                timestamps.push(ts);
                rawValues.push(val);
            }
        }

        if (rawValues.length === 0) continue;

        var processedValues = rawValues.slice();
        var processedTimestamps = timestamps.slice();

        // Apply outlier removal using library
        if (removeOutliers && utils.dataProcessing) {
            var outlierResult = utils.dataProcessing.removeOutliers(processedValues, processedTimestamps, {
                method: outlierMethod,
                iqrMultiplier: outlierIqrMultiplier,
                zscoreThreshold: outlierZscoreThreshold,
                minValue: outlierMinValue,
                maxValue: outlierMaxValue
            });
            processedValues = outlierResult.values;
            processedTimestamps = outlierResult.timestamps;
        }

        if (processedValues.length === 0) continue;

        // Apply smoothing using library
        if (smoothingEnabled && smoothingWindowMinutes > 0 && processedTimestamps.length > 1 && utils.dataProcessing) {
            var windowSize = utils.dataProcessing.getWindowSizeFromMinutes(processedTimestamps, smoothingWindowMinutes);
            processedValues = utils.dataProcessing.movingAverage(processedValues, windowSize);
        }

        // Build aggregated data for heatmap
        for (var k = 0; k < processedValues.length; k++) {
            var xKey;
            if (xAxisMode === 'time') {
                xKey = getTimeGroupKey(processedTimestamps[k], timeGrouping);
            } else {
                xKey = new Date(processedTimestamps[k]).toLocaleDateString();
            }

            var yKey = entityLabel;

            if (!aggregated[xKey]) aggregated[xKey] = {};
            if (!aggregated[xKey][yKey]) aggregated[xKey][yKey] = { sum: 0, count: 0, values: [] };

            aggregated[xKey][yKey].sum += processedValues[k];
            aggregated[xKey][yKey].count++;
            aggregated[xKey][yKey].values.push(processedValues[k]);
        }

        // Calculate statistics for this series using library
        var stats = utils.stats.calculate(processedValues);
        stats.current = processedValues[processedValues.length - 1];
        stats.lastTimestamp = processedTimestamps[processedTimestamps.length - 1];

        allStats.push({
            label: entityLabel,
            units: seriesUnits,
            decimals: seriesDecimals,
            color: seriesColor,
            stats: stats
        });
    }

    // Sort x categories appropriately
    xCategories = Object.keys(aggregated);
    if (xAxisMode === 'time') {
        if (timeGrouping === 'hour') {
            xCategories.sort(function(a, b) { return parseInt(a) - parseInt(b); });
        } else if (timeGrouping === 'dayOfWeek') {
            var dayOrder = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
            xCategories.sort(function(a, b) { return dayOrder.indexOf(a) - dayOrder.indexOf(b); });
        } else if (timeGrouping === 'month') {
            var monthOrder = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
            xCategories.sort(function(a, b) { return monthOrder.indexOf(a) - monthOrder.indexOf(b); });
        } else {
            xCategories.sort();
        }
    } else {
        xCategories.sort();
    }

    // Build heatmap data array
    for (var xi = 0; xi < xCategories.length; xi++) {
        for (var yi = 0; yi < yCategories.length; yi++) {
            var xKey = xCategories[xi];
            var yKey = yCategories[yi];
            var val = null;

            if (aggregated[xKey] && aggregated[xKey][yKey]) {
                val = aggregated[xKey][yKey].sum / aggregated[xKey][yKey].count;
            }

            if (val !== null) {
                heatmapData.push([xi, yi, val]);
                allValues.push(val);
            }
        }
    }

    if (heatmapData.length === 0) {
        showNoData('No valid heatmap data');
        return;
    }

    // Calculate data range using library
    var dataStats = utils.stats.calculate(allValues);
    var dataMin = minValue !== undefined && minValue !== null ? minValue : dataStats.min;
    var dataMax = maxValue !== undefined && maxValue !== null ? maxValue : dataStats.max;
    var colorRange = getColorScheme(colorScheme, minColor, maxColor);

    // Determine if using card-style legend
    var useStatsCard = showLegend && legendStyle === 'card';

    // Calculate margins based on legend position and style
    var legendMargin = useStatsCard ? 0 : (showLegend ? 35 : 0);
    var topMargin = 20;
    var bottomMargin = showDataZoomSlider ? 60 : 40;
    var leftMargin = 80;
    var rightMargin = showVisualMap ? 100 : 20;

    if (!useStatsCard && showLegend) {
        if (legendPosition === 'top') topMargin += legendMargin;
        if (legendPosition === 'bottom') bottomMargin += legendMargin;
        if (legendPosition === 'left') leftMargin += legendMargin;
        if (legendPosition === 'right') rightMargin += legendMargin;
    }

    // Build toolbox config using library pattern
    var toolboxConfig = buildToolboxConfig(showToolbox, toolboxFeatures);

    var option = {
        tooltip: showTooltip ? {
            formatter: function(params) {
                var formattedVal = utils.format.value(params.data[2], 2);
                return xCategories[params.data[0]] + ' / ' + yCategories[params.data[1]] + '<br/>' +
                    'Value: ' + formattedVal + ' ' + units;
            }
        } : {},
        toolbox: toolboxConfig,
        grid: {
            left: leftMargin,
            right: rightMargin,
            top: topMargin,
            bottom: bottomMargin
        },
        xAxis: {
            type: 'category',
            data: xCategories,
            axisLabel: { fontSize: 9, rotate: xCategories.length > 12 ? 45 : 0 },
            splitArea: { show: true }
        },
        yAxis: {
            type: 'category',
            data: yCategories,
            axisLabel: { fontSize: 10 },
            splitArea: { show: true }
        },
        visualMap: showVisualMap ? {
            min: dataMin,
            max: dataMax,
            calculable: true,
            orient: 'vertical',
            right: 10,
            top: 'center',
            inRange: {
                color: colorRange
            },
            text: [units ? 'High (' + units + ')' : 'High', 'Low'],
            textStyle: { fontSize: 10 }
        } : {
            show: false,
            min: dataMin,
            max: dataMax,
            inRange: { color: colorRange }
        },
        dataZoom: [
            { type: 'inside', xAxisIndex: 0 },
            showDataZoomSlider ? { type: 'slider', xAxisIndex: 0, bottom: 5, height: 20 } : null
        ].filter(function(x) { return x !== null; }),
        series: [{
            name: 'Heatmap',
            type: 'heatmap',
            data: heatmapData,
            label: {
                show: heatmapData.length <= 100,
                fontSize: 9,
                formatter: function(params) {
                    return utils.format.value(params.data[2], 1);
                }
            },
            emphasis: {
                itemStyle: {
                    shadowBlur: 10,
                    shadowColor: 'rgba(0, 0, 0, 0.5)'
                }
            }
        }]
    };

    chart.setOption(option, true);

    // Render statistics cards using library
    if (useStatsCard && allStats.length > 0) {
        utils.statsCard.render(statsCardContainers, {
            showLegend: showLegend,
            legendStyle: legendStyle,
            legendAlign: legendAlign,
            legendCardColorMode: legendCardColorMode,
            legendCardColor: legendCardColor,
            legendValues: legendValues,
            legendPosition: legendPosition,
            showTimestamp: showTimestamp,
            timestampFormat: timestampFormat,
            allStats: allStats
        });
    } else {
        // Hide all stats card containers if not using card style
        ['top', 'bottom', 'left', 'right'].forEach(function(pos) {
            var container = statsCardContainers[pos];
            if (container) {
                container.style.display = 'none';
            }
        });
    }

    // Resize chart after stats cards may have changed layout
    function doResize() {
        if (chart && chartContainer) {
            chart.resize({
                width: chartContainer.offsetWidth,
                height: chartContainer.offsetHeight
            });
        }
    }
    requestAnimationFrame(function() { requestAnimationFrame(doResize); });
    setTimeout(doResize, 100);
    setTimeout(doResize, 250);
}

function showNoData(message) {
    if (!chart) return;
    chart.setOption({
        title: {
            text: message,
            left: 'center',
            top: 'middle',
            textStyle: { color: '#999', fontSize: 14 }
        }
    }, true);

    // Hide all stats card containers
    ['top', 'bottom', 'left', 'right'].forEach(function(pos) {
        var container = statsCardContainers[pos];
        if (container) {
            container.style.display = 'none';
        }
    });
}

function buildToolboxConfig(showToolbox, toolboxFeatures) {
    if (!showToolbox) return null;

    var features = toolboxFeatures || [];
    var toolbox = { show: true, right: 10, top: 5, feature: {} };

    if (features.indexOf('saveAsImage') !== -1) {
        toolbox.feature.saveAsImage = { show: true, title: 'Save as PNG', pixelRatio: 2 };
    }

    if (features.indexOf('dataView') !== -1) {
        toolbox.feature.dataView = {
            show: true,
            title: 'Data View',
            readOnly: true,
            lang: ['Data View', 'Close', 'Refresh']
        };
    }

    if (features.indexOf('dataZoom') !== -1) {
        toolbox.feature.dataZoom = { show: true, title: { zoom: 'Zoom', back: 'Reset Zoom' } };
    }

    if (features.indexOf('restore') !== -1) {
        toolbox.feature.restore = { show: true, title: 'Restore' };
    }

    return toolbox;
}

self.onResize = function() {
    if (chart && chartContainer) {
        chart.resize({
            width: chartContainer.offsetWidth,
            height: chartContainer.offsetHeight
        });
    }
};

self.onDestroy = function() {
    // Unsubscribe from zoom sync
    if (utils && utils.zoomSync && widgetId) {
        utils.zoomSync.unsubscribe(widgetId);
    }

    if (resizeObserver) {
        resizeObserver.disconnect();
        resizeObserver = null;
    }
    if (chart) {
        chart.off('datazoom');
        chart.dispose();
        chart = null;
    }
    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }
};

self.typeParameters = function() {
    return {
        previewWidth: '80%',
        previewHeight: '100%',
        embedTitlePanel: false,
        hasDataExportAction: true,
        dataKeySettingsFunction: function() { return {}; },
        defaultDataKeysFunction: function() {
            return [{ name: 'value', label: 'Value', type: 'timeseries' }];
        }
    };
};

