descriptor.controllerScript:
/**
 * ECO Line with Confidence Band Widget (Library Version)
 *
 * Time series line chart with confidence band (upper/lower bounds).
 * Supports statistics cards, outlier removal, smoothing, and toolbox.
 *
 * Uses ECOWidgetUtils library for common functionality.
 */

var utils = window.ECOWidgetUtils;

var chart = null;
var chartContainer = null;
var statsCardContainers = {};
var timewindowSelectorContainer = null;
var resizeObserver = null;
var zoomDebounceTimer = null;
var isExternalUpdate = false;

self.onInit = function() {
    chartContainer = self.ctx.$container.find('#chart-container')[0];
    timewindowSelectorContainer = self.ctx.$container.find('#timewindow-selector')[0];
    statsCardContainers = {
        top: self.ctx.$container.find('#stats-card-top')[0],
        bottom: self.ctx.$container.find('#stats-card-bottom')[0],
        left: self.ctx.$container.find('#stats-card-left')[0],
        right: self.ctx.$container.find('#stats-card-right')[0]
    };

    if (!chartContainer) {
        console.error('ECO Line Confidence Band: Chart container not found');
        return;
    }

    if (typeof echarts === 'undefined') {
        console.error('ECO Line Confidence Band: ECharts library not loaded');
        return;
    }

    if (!utils) {
        console.error('ECO Line Confidence Band: ECOWidgetUtils library not loaded');
        return;
    }

    chart = echarts.init(chartContainer);

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync !== false) {
        chart.on('datazoom', handleZoomSync);
    }

    updateChart();

    // Multiple delayed resizes for ThingsBoard's async layout
    [100, 250, 500, 1000].forEach(function(delay) {
        setTimeout(function() {
            if (chart && chartContainer) {
                chart.resize({
                    width: chartContainer.offsetWidth,
                    height: chartContainer.offsetHeight
                });
            }
        }, delay);
    });

    // ResizeObserver for dynamic container changes
    if (typeof ResizeObserver !== 'undefined') {
        resizeObserver = new ResizeObserver(function() {
            requestAnimationFrame(function() {
                if (chart && chartContainer) {
                    chart.resize({
                        width: chartContainer.offsetWidth,
                        height: chartContainer.offsetHeight
                    });
                }
            });
        });
        resizeObserver.observe(chartContainer);
    }
};

function handleZoomSync(params) {
    if (isExternalUpdate) return;

    var settings = self.ctx.settings || {};
    if (settings.enableZoomSync === false) return;

    var debounceMs = settings.zoomSyncDebounce || 150;

    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }

    zoomDebounceTimer = setTimeout(function() {
        var option = chart.getOption();
        var dataZoom = option.dataZoom && option.dataZoom[0];
        if (!dataZoom) return;

        var timeWindow = self.ctx.timeWindow;
        if (!timeWindow) return;

        var totalRange = timeWindow.maxTime - timeWindow.minTime;
        var startPercent = dataZoom.start || 0;
        var endPercent = dataZoom.end || 100;

        if (startPercent > 0.5 || endPercent < 99.5) {
            var startTime = Math.round(timeWindow.minTime + (totalRange * startPercent / 100));
            var endTime = Math.round(timeWindow.minTime + (totalRange * endPercent / 100));

            if (self.ctx.dashboard && self.ctx.dashboard.onUpdateTimewindow) {
                self.ctx.dashboard.onUpdateTimewindow(startTime, endTime);
            }
        }
    }, debounceMs);
}

self.onDataUpdated = function() {
    isExternalUpdate = true;
    updateChart();
    setTimeout(function() { isExternalUpdate = false; }, 100);
};

function updateChart() {
    if (!chart) return;

    var settings = self.ctx.settings || {};
    var data = self.ctx.data || [];

    // Chart settings
    var lineColor = settings.lineColor || '#2196F3';
    var bandColor = settings.bandColor || '#2196F3';
    var bandOpacity = settings.bandOpacity !== undefined ? settings.bandOpacity : 0.2;
    var smoothLine = settings.smoothLine !== false;
    var showDataZoomSlider = settings.showDataZoomSlider !== false;
    var showTooltip = settings.showTooltip !== false;

    // Legend settings
    var showLegend = settings.showLegend !== false;
    var legendStyle = settings.legendStyle || 'classic';
    var legendPosition = settings.legendPosition || 'bottom';
    var legendAlign = settings.legendAlign || 'center';
    var legendCardColorMode = settings.legendCardColorMode || 'auto';
    var legendCardColor = settings.legendCardColor || '#2196F3';
    var legendValues = settings.legendValues || ['current'];

    // Timestamp settings
    var showTimestamp = settings.showTimestamp !== false;
    var timestampFormat = settings.timestampFormat || 'YYYY-MM-DD HH:mm:ss';

    // Y-Axis settings
    var yAxisMin = settings.yAxisMin;
    var yAxisMax = settings.yAxisMax;

    // Outlier removal settings
    var removeOutliers = settings.removeOutliers === true;
    var outlierMethod = settings.outlierMethod || 'iqr';
    var outlierIqrMultiplier = settings.outlierIqrMultiplier || 1.5;
    var outlierZscoreThreshold = settings.outlierZscoreThreshold || 3;
    var outlierMinValue = settings.outlierMinValue;
    var outlierMaxValue = settings.outlierMaxValue;

    // Smoothing settings
    var smoothingEnabled = settings.smoothingEnabled === true;
    var smoothingWindowMinutes = settings.smoothingWindowMinutes || 15;

    // Toolbox settings
    var showToolbox = settings.showToolbox !== false;
    var toolboxFeatures = settings.toolboxFeatures || ['saveAsImage', 'dataView', 'dataZoom', 'restore'];

    // Timewindow selector settings
    var showTimewindowSelector = settings.showTimewindowSelector === true;

    // Initialize and render timewindow selector using library
    if (showTimewindowSelector && self.ctx.dashboard && timewindowSelectorContainer) {
        utils.timewindow.init(timewindowSelectorContainer, {
            color: settings.twSelectorColor || '',
            position: settings.twSelectorPosition || 'center',
            dayFormat: settings.twSelectorDayFormat || 'D MMM YYYY',
            weekFormat: settings.twSelectorWeekFormat || 'D-D MMM',
            monthFormat: settings.twSelectorMonthFormat || 'MMM YYYY',
            customStartTime: settings.twCustomStartTime || '',
            customEndTime: settings.twCustomEndTime || '',
            aggregationType: settings.twAggregationType || 'NONE',
            maxDataPoints: settings.twMaxDataPoints || 100000
        }, self.ctx);
        utils.timewindow.render();
    } else if (timewindowSelectorContainer) {
        utils.timewindow.hide();
    }

    // Check for valid data
    if (!data.length) {
        showNoData('No data available');
        return;
    }

    // Find value, upper, lower data keys
    var valueData = null, upperData = null, lowerData = null;
    var units = '';
    var decimals = 2;

    for (var i = 0; i < data.length; i++) {
        var ds = data[i];
        var keyName = (ds.dataKey && ds.dataKey.name) || '';
        var keyLabel = (ds.dataKey && ds.dataKey.label) || keyName;

        if (keyName === 'value' || keyLabel.toLowerCase() === 'value' || i === 0) {
            if (!valueData) {
                valueData = ds;
                units = (ds.dataKey && ds.dataKey.units) || '';
                decimals = (ds.dataKey && ds.dataKey.decimals !== undefined) ? ds.dataKey.decimals : 2;
            }
        }
        if (keyName === 'upper' || keyLabel.toLowerCase().includes('upper')) {
            upperData = ds;
        }
        if (keyName === 'lower' || keyLabel.toLowerCase().includes('lower')) {
            lowerData = ds;
        }
    }

    // If no explicit upper/lower, check if we have 3 series
    if (!upperData && !lowerData && data.length >= 3) {
        valueData = data[0];
        upperData = data[1];
        lowerData = data[2];
    } else if (!upperData && !lowerData && data.length === 2) {
        valueData = data[0];
        upperData = data[1];
    }

    if (!valueData || !valueData.data || !valueData.data.length) {
        showNoData('No valid data points');
        return;
    }

    var valueLabel = (valueData.dataKey && valueData.dataKey.label) || 'Value';

    // Extract values and timestamps for main data
    var rawValues = [];
    var timestamps = [];
    for (var j = 0; j < valueData.data.length; j++) {
        var ts = valueData.data[j][0];
        var val = valueData.data[j][1];
        if (val !== null && !isNaN(val)) {
            timestamps.push(ts);
            rawValues.push(val);
        }
    }

    if (rawValues.length === 0) {
        showNoData('No valid data points');
        return;
    }

    var values = rawValues.slice();
    var processedTimestamps = timestamps.slice();

    // Remove outliers using library
    if (removeOutliers) {
        var outlierResult = utils.dataProcessing.removeOutliers(values, processedTimestamps, {
            method: outlierMethod,
            iqrMultiplier: outlierIqrMultiplier,
            zscoreThreshold: outlierZscoreThreshold,
            minValue: outlierMinValue,
            maxValue: outlierMaxValue
        });
        values = outlierResult.values;
        processedTimestamps = outlierResult.timestamps;
        if (values.length === 0) {
            showNoData('All data filtered as outliers');
            return;
        }
    }

    // Apply smoothing using library
    if (smoothingEnabled && smoothingWindowMinutes > 0 && processedTimestamps.length > 1) {
        var windowSize = utils.dataProcessing.getWindowSizeFromMinutes(processedTimestamps, smoothingWindowMinutes);
        values = utils.dataProcessing.movingAverage(values, windowSize);
    }

    // Build main line data
    var mainLineData = [];
    var lastTimestamp = null;
    var lastValue = null;
    for (var k = 0; k < values.length; k++) {
        mainLineData.push([processedTimestamps[k], values[k]]);
        lastTimestamp = processedTimestamps[k];
        lastValue = values[k];
    }

    // Calculate statistics using library
    var stats = utils.stats.calculate(values);
    stats.current = lastValue;
    stats.lastTimestamp = lastTimestamp;

    var allStats = [{
        label: valueLabel,
        units: units,
        decimals: decimals,
        color: lineColor,
        stats: stats
    }];

    var series = [];
    var legendData = [valueLabel];

    // Process upper/lower bounds for confidence band
    if (upperData && lowerData && upperData.data && lowerData.data) {
        // Process lower data (may also need outlier removal/smoothing)
        var lowerValues = [];
        var lowerTimestamps = [];
        for (var li = 0; li < lowerData.data.length; li++) {
            var lts = lowerData.data[li][0];
            var lval = lowerData.data[li][1];
            if (lval !== null && !isNaN(lval)) {
                lowerTimestamps.push(lts);
                lowerValues.push(lval);
            }
        }

        if (removeOutliers && lowerValues.length > 0) {
            var lowerOutlierResult = utils.dataProcessing.removeOutliers(lowerValues, lowerTimestamps, {
                method: outlierMethod,
                iqrMultiplier: outlierIqrMultiplier,
                zscoreThreshold: outlierZscoreThreshold,
                minValue: outlierMinValue,
                maxValue: outlierMaxValue
            });
            lowerValues = lowerOutlierResult.values;
            lowerTimestamps = lowerOutlierResult.timestamps;
        }

        if (smoothingEnabled && smoothingWindowMinutes > 0 && lowerTimestamps.length > 1) {
            var lowerWindowSize = utils.dataProcessing.getWindowSizeFromMinutes(lowerTimestamps, smoothingWindowMinutes);
            lowerValues = utils.dataProcessing.movingAverage(lowerValues, lowerWindowSize);
        }

        var lowerLine = [];
        for (var lk = 0; lk < lowerValues.length; lk++) {
            lowerLine.push([lowerTimestamps[lk], lowerValues[lk]]);
        }

        // Process upper data
        var upperValues = [];
        var upperTimestamps = [];
        for (var ui = 0; ui < upperData.data.length; ui++) {
            var uts = upperData.data[ui][0];
            var uval = upperData.data[ui][1];
            if (uval !== null && !isNaN(uval)) {
                upperTimestamps.push(uts);
                upperValues.push(uval);
            }
        }

        if (removeOutliers && upperValues.length > 0) {
            var upperOutlierResult = utils.dataProcessing.removeOutliers(upperValues, upperTimestamps, {
                method: outlierMethod,
                iqrMultiplier: outlierIqrMultiplier,
                zscoreThreshold: outlierZscoreThreshold,
                minValue: outlierMinValue,
                maxValue: outlierMaxValue
            });
            upperValues = upperOutlierResult.values;
            upperTimestamps = upperOutlierResult.timestamps;
        }

        if (smoothingEnabled && smoothingWindowMinutes > 0 && upperTimestamps.length > 1) {
            var upperWindowSize = utils.dataProcessing.getWindowSizeFromMinutes(upperTimestamps, smoothingWindowMinutes);
            upperValues = utils.dataProcessing.movingAverage(upperValues, upperWindowSize);
        }

        // Build band data (upper - lower)
        var upperMap = {};
        for (var um = 0; um < upperTimestamps.length; um++) {
            upperMap[upperTimestamps[um]] = upperValues[um];
        }

        var bandData = [];
        for (var bd = 0; bd < lowerTimestamps.length; bd++) {
            var bts = lowerTimestamps[bd];
            var low = lowerValues[bd];
            var high = upperMap[bts];
            if (high !== undefined && low !== undefined) {
                bandData.push([bts, high - low]);
            }
        }

        // Lower bound line (invisible, serves as stack base)
        series.push({
            name: 'Lower Bound',
            type: 'line',
            data: lowerLine,
            smooth: smoothLine,
            symbol: 'none',
            lineStyle: { opacity: 0 },
            areaStyle: { opacity: 0 },
            stack: 'confidence',
            z: 1
        });

        // Band (stacked on lower)
        series.push({
            name: 'Confidence Band',
            type: 'line',
            data: bandData,
            smooth: smoothLine,
            symbol: 'none',
            lineStyle: { opacity: 0 },
            areaStyle: {
                color: bandColor,
                opacity: bandOpacity
            },
            stack: 'confidence',
            z: 2
        });

        legendData.push('Confidence Band');
    } else if (upperData && upperData.data) {
        // Only upper bound - show as separate line
        var upperOnlyData = upperData.data.map(function(d) { return [d[0], d[1]]; });
        series.push({
            name: 'Upper Bound',
            type: 'line',
            data: upperOnlyData,
            smooth: smoothLine,
            symbol: 'none',
            lineStyle: { color: bandColor, type: 'dashed', width: 1 }
        });
        legendData.push('Upper Bound');
    }

    // Main value line (on top)
    series.push({
        name: valueLabel,
        type: 'line',
        data: mainLineData,
        smooth: smoothLine,
        symbol: 'none',
        lineStyle: { color: lineColor, width: 2 },
        z: 10
    });

    // Build legend/grid margins
    var useStatsCard = legendStyle === 'card';
    var legendMargin = (showLegend && !useStatsCard) ? 35 : 0;

    var topMargin = 40;
    var bottomMargin = showDataZoomSlider ? 60 : 40;
    var leftMargin = 60;
    var rightMargin = showToolbox ? 80 : 20;

    if (!useStatsCard) {
        if (legendPosition === 'top') topMargin += legendMargin;
        if (legendPosition === 'bottom') bottomMargin += legendMargin;
        if (legendPosition === 'left') leftMargin += legendMargin;
        if (legendPosition === 'right') rightMargin += legendMargin;
    }

    // Build legend config
    var legendConfig = null;
    if (showLegend && !useStatsCard) {
        legendConfig = { data: legendData, textStyle: { fontSize: 11 } };
        switch (legendPosition) {
            case 'top': legendConfig.top = 5; legendConfig.left = 'center'; legendConfig.orient = 'horizontal'; break;
            case 'bottom': legendConfig.bottom = showDataZoomSlider ? 30 : 5; legendConfig.left = 'center'; legendConfig.orient = 'horizontal'; break;
            case 'left': legendConfig.left = 5; legendConfig.top = 'middle'; legendConfig.orient = 'vertical'; break;
            case 'right': legendConfig.right = 5; legendConfig.top = 'middle'; legendConfig.orient = 'vertical'; break;
            default: legendConfig.bottom = showDataZoomSlider ? 30 : 5; legendConfig.left = 'center'; legendConfig.orient = 'horizontal';
        }
    }

    // Build toolbox config
    var toolboxConfig = null;
    if (showToolbox) {
        toolboxConfig = { show: true, right: 10, top: 5, feature: {} };

        if (toolboxFeatures.indexOf('saveAsImage') !== -1) {
            toolboxConfig.feature.saveAsImage = { show: true, title: 'Save as PNG', pixelRatio: 2 };
        }

        if (toolboxFeatures.indexOf('dataView') !== -1) {
            toolboxConfig.feature.dataView = {
                show: true,
                title: 'Data View',
                readOnly: true,
                lang: ['Data View', 'Close', 'Refresh'],
                optionToContent: function(opt) {
                    var html = '<div style="padding: 12px; font-family: -apple-system, BlinkMacSystemFont, sans-serif;">';
                    (opt.series || []).forEach(function(s) {
                        if (s.name === 'Lower Bound') return;
                        var sdata = s.data || [];
                        if (sdata.length === 0) return;
                        html += '<div style="font-size: 14px; font-weight: 600; margin: 12px 0 8px 0;">' + s.name + ' (' + sdata.length + ' points)</div>';
                        html += '<div style="max-height: 300px; overflow: auto; border: 1px solid #e5e7eb; border-radius: 6px;">';
                        html += '<table style="width: 100%; border-collapse: collapse; font-size: 12px;">';
                        html += '<thead style="position: sticky; top: 0; background: #f9fafb;">';
                        html += '<tr><th style="padding: 8px; text-align: left; border-bottom: 2px solid #e5e7eb;">Timestamp</th>';
                        html += '<th style="padding: 8px; text-align: right; border-bottom: 2px solid #e5e7eb;">Value</th></tr></thead><tbody>';
                        sdata.forEach(function(row, idx) {
                            var d = new Date(row[0]);
                            var tsStr = d.getFullYear() + '-' + String(d.getMonth() + 1).padStart(2, '0') + '-' + String(d.getDate()).padStart(2, '0') + ' ' +
                                     String(d.getHours()).padStart(2, '0') + ':' + String(d.getMinutes()).padStart(2, '0') + ':' + String(d.getSeconds()).padStart(2, '0');
                            html += '<tr style="background: ' + (idx % 2 === 0 ? '#ffffff' : '#f9fafb') + ';">';
                            html += '<td style="padding: 6px 8px; font-family: monospace; font-size: 11px;">' + tsStr + '</td>';
                            html += '<td style="padding: 6px 8px; text-align: right; font-weight: 500;">' + (row[1] !== null ? row[1].toFixed(2) : '-') + '</td></tr>';
                        });
                        html += '</tbody></table></div>';
                    });
                    html += '</div>';
                    return html;
                }
            };
        }

        if (toolboxFeatures.indexOf('dataZoom') !== -1) {
            toolboxConfig.feature.dataZoom = { show: true, title: { zoom: 'Zoom', back: 'Reset Zoom' } };
        }

        if (toolboxFeatures.indexOf('restore') !== -1) {
            toolboxConfig.feature.restore = { show: true, title: 'Restore' };
        }
    }

    // Build tooltip config
    var tooltipConfig = showTooltip ? {
        trigger: 'axis',
        axisPointer: { type: 'cross' },
        formatter: function(params) {
            if (!params || !params.length) return '';
            var d = new Date(params[0].value[0]);
            var result = d.toLocaleString() + '<br/>';
            for (var pi = 0; pi < params.length; pi++) {
                var p = params[pi];
                if (p.value && p.value[1] !== null && p.value[1] !== undefined && p.seriesName !== 'Lower Bound') {
                    result += '<span style="display:inline-block;margin-right:4px;border-radius:10px;width:10px;height:10px;background-color:' + (p.color || lineColor) + ';"></span>';
                    result += p.seriesName + ': ' + utils.format.value(p.value[1], decimals) + ' ' + units + '<br/>';
                }
            }
            return result;
        }
    } : {};

    // DataZoom config
    var dataZoomConfig = [{ type: 'inside', xAxisIndex: 0 }];
    if (showDataZoomSlider) {
        dataZoomConfig.push({ type: 'slider', xAxisIndex: 0, bottom: 5, height: 20 });
    }

    var option = {
        tooltip: tooltipConfig,
        legend: legendConfig,
        toolbox: toolboxConfig,
        grid: { left: leftMargin, right: rightMargin, top: topMargin, bottom: bottomMargin },
        xAxis: { type: 'time', axisLabel: { fontSize: 10 }, splitLine: { show: false } },
        yAxis: {
            type: 'value',
            name: units,
            min: yAxisMin,
            max: yAxisMax,
            axisLabel: { fontSize: 10 },
            splitLine: { lineStyle: { type: 'dashed', opacity: 0.3 } }
        },
        dataZoom: dataZoomConfig,
        series: series
    };

    chart.setOption(option, true);

    // Render statistics cards using library
    utils.statsCard.render(statsCardContainers, {
        showLegend: showLegend,
        legendStyle: legendStyle,
        legendAlign: legendAlign,
        legendCardColorMode: legendCardColorMode,
        legendCardColor: legendCardColor,
        legendValues: legendValues,
        legendPosition: legendPosition,
        showTimestamp: showTimestamp,
        timestampFormat: timestampFormat,
        allStats: allStats
    });

    // Resize chart after stats cards
    function doResize() {
        if (chart && chartContainer) {
            chart.resize({
                width: chartContainer.offsetWidth,
                height: chartContainer.offsetHeight
            });
        }
    }
    requestAnimationFrame(function() { requestAnimationFrame(doResize); });
    setTimeout(doResize, 100);
    setTimeout(doResize, 250);
}

function showNoData(message) {
    if (!chart) return;
    chart.setOption({
        title: {
            text: message,
            left: 'center',
            top: 'middle',
            textStyle: { color: '#999', fontSize: 14 }
        }
    }, true);
}

self.onResize = function() {
    if (chart && chartContainer) {
        chart.resize({
            width: chartContainer.offsetWidth,
            height: chartContainer.offsetHeight
        });
    }
};

self.onDestroy = function() {
    if (resizeObserver) {
        resizeObserver.disconnect();
        resizeObserver = null;
    }
    if (chart) {
        chart.off('datazoom');
        chart.dispose();
        chart = null;
    }
    if (zoomDebounceTimer) {
        clearTimeout(zoomDebounceTimer);
    }
};

self.typeParameters = function() {
    return {
        previewWidth: '100%',
        previewHeight: '100%',
        embedTitlePanel: false,
        hasDataExportAction: true,
        dataKeySettingsFunction: function() { return {}; },
        defaultDataKeysFunction: function() {
            return [
                { name: 'value', label: 'Value', type: 'timeseries', units: '', decimals: 1 },
                { name: 'upper', label: 'Upper', type: 'timeseries', units: '', decimals: 1 },
                { name: 'lower', label: 'Lower', type: 'timeseries', units: '', decimals: 1 }
            ];
        }
    };
};

